export  function fetchCases(){
    const url="https://api.covid19api.com/summary"
    return(dispatch)=>{
        fetch(url)
        .then((response)=>{
           return response.json()
        }).then((data)=>{
              console.log("World-wide-Cases",data.Countries)
            
        })

    }
}
