import { ADD_CASES } from "../actions/actionType";

const initialState={
    allcases:[],
    countryWise:[]
}
export default function cases(state=initialState,action){
    if(action.type===ADD_CASES){
        return{
            ...state,
            countryWise:action.cases

        }
    }
    return state;
}
